            return (number)*this.factorial(number-1);
          }
    },
    /*å†’æ³¡æ’åº*/
    bubbling(arr){
      for(var j=0;j<arr.length-1;j++){
        for(var i=0;i<arr.length;i++){
          if(arr[i]>arr[i+1]){
                var  tepm = arr[i]
              arr[i] = arr[i+1];
              arr[i+1] = tepm;
          }
        }
      }
      return arr;
    },
	//æ‰¾åˆ°æ•°ç»„ä¸­æœ€å¤§çš„ä¸‰ä¸ª 
    findMax3(){
